import { Component, OnInit } from '@angular/core';
import {HttpClient} from "@angular/common/http"


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styles: [
    `
      .list-group-item:first-child {
        border-top-left-radius: 0;
        border-top-right-radius: 0;
        border-top: 0;
      }
    `
  ]
})
export class AppComponent implements OnInit {
  query: string;
  artists: Object;
  currentArtist: Object; 

  showArtist(item: { name: string; highlight: boolean; }) {
    this.query = item.name;
    item.highlight = !item.highlight
    this.currentArtist = item; 
  }

  constructor(private http : HttpClient){
    this.query = '';
    this.artists = []
  }
  ngOnInit(): void {
    this.http.get<Object>('../assets/data.json').subscribe(data=>{
      this.artists = data; 
    })
  }
}
<div class="position-relative container">
  <div class="row justify-content-center">
    <div class="card mt-sm-3 col-md-8">
      <div class="card-body">
        <h3 class="mb-0">Artist Directory</h3>
        <div class="form-group">
          <div class="form-label" *ngIf="query"><strong>For:{{query}}</strong>
          </div>
          <input [(ngModel)]="query" class="form-control mt-2" type="text">
        </div><!-- form-group -->
      </div><!-- card-body -->
    </div><!-- card -->
  </div><!-- row -->

  <div class="position-absolute container" style="left: 0; z-index:10">
    <div class="row justify-content-center">
      <div class="col-sm-10 col-md-8 col-lg-6 col-xl-5">
        <div class="list-group" *ngIf="query">
          <div class="list-group-item list-group-item-action flex-column align-items-start"
            *ngFor="let artist of (artists | searchItem : query)"
            (click)="showArtist(artist)" query="" [style.backgroundColor]="artist.highlight ? '#eee' : '#fff'">
              <app-app-artist-item [artist]="artist"></app-app-artist-item>
            </div><!-- link -->
        </div><!-- list-group -->
      </div><!-- col -->
    </div><!-- row -->
  </div><!-- container -->
  <app-artist-details *ngIf="currentArtist" [artist]="currentArtist"></app-artist-details>
</div><!-- position -->
